import image10 from './Images/10.jpeg';
import image9 from './Images/9.jpeg';
import image8 from './Images/8.jpeg';
import image7 from './Images/7.jpeg';
import image6 from './Images/6.jpeg';
import image5 from './Images/5.jpeg';
import image4 from './Images/4.jpeg';
import image3 from './Images/3.jpeg';
import image2 from './Images/2.jpeg';
import image1 from './Images/1.jpeg';


class App extends Component {

  constructor(props) {
    super(props);
    this.state = {WhichRecord: 25}
  }

  RandomNumber = () => {
    this.setState((state, props) => ({
      WhichRecord: Math.floor((Math.random()*25))+1
    }));
    console.log(this.state.WhichRecord);
  };

  render() {
    var ImageArray = [26];


    ImageArray[25] = [image25, "James Brown"];
    ImageArray[24] = [image24, "Stevie Wonder"];
    ImageArray[23] = [image23, "John Lennon"];
    ImageArray[22] = [image22, "Robert Johnson"];
    ImageArray[21] = [image21, "Chuck Berry"];
    ImageArray[20] = [image20, "Michael Jackson"];
    ImageArray[19] = [image19, "Van Morrison"];
    ImageArray[18] = [image18, "Bruce Springsteen"];
    ImageArray[17] = [image17, "Nirvana"];
    ImageArray[16] = [image16, "Bob Dylan"];
    ImageArray[15] = [image15, "The Jimmy Hendrix Experience"];
    ImageArray[14] = [image14, "The Beatles"];
    ImageArray[13] = [image13, "The Velvet Underground and Nico"];
    ImageArray[12] = [image12, "Miles Davis"];
    ImageArray[11] = [image11, "Elvis Presley"];
    ImageArray[10] = [image10, "The Beatles"];
    ImageArray[9] = [image9, "Bob Dylan"];
    ImageArray[8] = [image8, "The Clash"];
    ImageArray[7] = [image7, "The Rolling Stones"];
    ImageArray[6] = [image6, "Marvin Gaye"];
    ImageArray[5] = [image5, "The Beatles"];
    ImageArray[4] = [image4, "Bob Dylan"];
    ImageArray[3] = [image3, "The Beatles"];
    ImageArray[2] = [image2, "The Beach Boys"];
    ImageArray[1] = [image1, "The Beatles"];


    var RandomNumber = Math.floor((Math.random()*25))+1;
    //console.log(RandomNumber);





  return (
    <div>
      <p>Hello World!</p>
      <img src = {ImageArray[this.state.WhichRecord]} width = "400px" height = "400px"/>
      <button onClick={this.RandomNumber}/>
    </div>
  );
}
}
export default App;
