import React, {Component} from 'react';
import logo from './logo.svg';
import './App.css';

import image25 from './Images/25.jpeg';
import image24 from './Images/24.jpeg';
import image23 from './Images/23.jpeg';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {WhichRecord: 25}
  }

  RandomNumber = () => {
    this.setState((state, props) => ({
      WhichRecord: Math.floor((Math.random()*25))+1
    }));
    console.log(this.state.WhichRecord);
  };

  render() {
    var ImageArray = [26];

    ImageArray[25] = image25;
    ImageArray[24] = image24;
    ImageArray[23] = image23;

    var RandomNumber = Math.floor((Math.random()*25))+1;
    //console.log(RandomNumber);





  return (
    <div>
      <p>Hello World!</p>
      <img src = {ImageArray[this.state.WhichRecord]} width = "400px" height = "400px"/>
      <button onClick={this.RandomNumber}/>
    </div>
  );
}
}
export default App;
