import image2 from './Images/2.jpeg';
import image1 from './Images/1.jpeg';


class App extends Component {

  constructor(props) {
    super(props);
    this.state = {WhichRecord: 25}
  }

  RandomNumber = () => {
    this.setState((state, props) => ({
      WhichRecord: Math.floor((Math.random()*25))+1
    }));
    console.log(this.state.WhichRecord);
  };

  render() {
    var ImageArray = [26][3];

    ImageArray[25][0] = image25;
    ImageArray[24] = image24;
    ImageArray[23] = image23;
    ImageArray[22] = image22;
    ImageArray[21] = image21;
    ImageArray[20] = image20;
    ImageArray[19] = image19;
    ImageArray[18] = image18;
    ImageArray[17] = image17;
    ImageArray[16] = image16;
    ImageArray[15] = image15;
    ImageArray[14] = image14;
    ImageArray[13] = image13;
    ImageArray[12] = image12;
    ImageArray[11] = image11;
    ImageArray[10] = image10;
    ImageArray[9] = image9;
    ImageArray[8] = image8;
    ImageArray[7] = image7;
    ImageArray[6] = image6;
    ImageArray[5] = image5;
    ImageArray[4] = image4;
    ImageArray[3] = image3;
    ImageArray[2] = image2;
    ImageArray[1] = image1;


    var RandomNumber = Math.floor((Math.random()*25))+1;
    //console.log(RandomNumber);





  return (
    <div>
      <p>Hello World!</p>
      <img src = {ImageArray[this.state.WhichRecord]} width = "400px" height = "400px"/>
      <button onClick={this.RandomNumber}/>
    </div>
  );
}
}
export default App;
